       .then(response => {
         if(response.data.success){
           setParticles(response.data.particles)
         } else{
           alert("Failed to fetch data")
         }
       })

       axios.get("/volcanoes/getGenerals")
       .then(res => {
        if(res.data.success){
            setGenerals(res.data.generals)
          } else{
            alert("Failed to fetch data")
          }
       })

     },[])


     useEffect(()=>{
        setFetchedData({
          Particles: particles,
          Generals: generals
        })
